        private void Button_Click(object sender, RoutedEventArgs e)
        {

            if (TextBoxSlBirth.Visibility== Visibility.Collapsed)
            {

                int y = GetInt(TextBoxSlBirthY.Text.Trim());
                if (y == 0)
                {
                    return;
                }
                else
                {
                    TextBlockTitleResult.Visibility = Visibility.Visible;
                    TextBlockResult.Visibility = Visibility.Visible;
                    switch (y)
                    {
                        case 1924:
                        case 1936:
                        case 1948:
                        case 1960:
                        case 1972:
                        case 1984:
                        case 1996:
                        case 2008:
                        case 2020:
                        case 2032:
