        int GetInt(string s)
        {
            try
            {
                return Convert.ToInt32(s);

            }
            catch
            {
                MessageBox.Show("Неверно введена дата. Повторите попытку.");
                return 0;
            }
        }
        DateTime GetDate(string s)
        {
            try
            {
                return Convert.ToDateTime(s);
                
            }
            catch
            {
                MessageBox.Show("Неверно введена дата. Повторите попытку.");
                return new DateTime(2024, 01, 01);

            }

        }

        private void Button_Click_1(object sender, RoutedEventArgs e)
        {
            TextBlockTitleResult.Visibility = Visibility.Collapsed;
            TextBlockResult.Text = "";
            if (GridMounth.Visibility == Visibility.Hidden)
            {
                GridMounth.Visibility = Visibility.Visible;
            }
            else GridMounth.Visibility = Visibility.Hidden;
        }
       
        Visibility visibilityResilt()
        {
            TextBlockTitleResult.Visibility = Visibility.Visible;
            TextBlockResult.Visibility = Visibility.Visible;
            if (GridMounth.Visibility == Visibility.Hidden)
            {
                return GridMounth.Visibility = Visibility.Visible;
            }
            else
            {
                return GridMounth.Visibility = Visibility.Hidden;
            }
        }
    }
